package Generic;

import java.util.ArrayList;
import java.util.List;

public class GenericDemo {
	//无限通配符可以允许传入任何泛型，你无法将任何类型的数据加入Collections<?>,但是你可以作为一个参数传入方法，并且这个方法不必改变此集合，你并不会知道你要得到什么类型的集合	//不同于原生态的Collections,Collections<?>是安全的
	public static void unsafeAdd(List<Object> list,Object o) {
		list.add(o);
	}
	
	public static void main(String[] args) {
		List<Object> strings = new ArrayList<Object>();
		unsafeAdd(strings,"sd");                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
		Object s =strings.get(0);
	}
}
